mapper.insert:

  SELECT LAST_INSERT_ID()

insert into WindowRecord (userID, window, time
  )
values (#{userid,jdbcType=INTEGER}, #{window,jdbcType=BIT}, #{time,jdbcType=TIMESTAMP}
  )

mapper.update:
update WindowRecord
set userID = #{userid,jdbcType=INTEGER},
  window = #{window,jdbcType=BIT},
  time = #{time,jdbcType=TIMESTAMP}
where wRecordID = #{wrecordid,jdbcType=BIGINT}

mapper.select:
select wRecordID, userID, window, time
from WindowRecord
where wRecordID = #{wrecordid,jdbcType=BIGINT}
